import React,  { useState } from 'react';

function SignUp () {
    const [data, setUser] = useState({ name:"", username:"", password:""});
    let name , value;
    const handleinput = (e) => {
      console.log(e)
        name = e.target.name;
        value = e.target.value;
        setUser({...data, [name]:value})
    };

    const PostData =  (e) => {
      e.preventDefault();
      const { name, email, password } = data;
      const res = fetch("/Register", {
        method: "POST", 
        header: {"Content-Type" : "application/json"},
        body: JSON.stringify({ 
          name, email, password
        })
      });
    }

    return (
        <>
        <div className="Form" method="POST"> 
        <h1>Form</h1> 
        <label> Name : </label>
        <input type="text" name="name" required value={data.name} onChange={handleinput} ></input>
        <label> Username or Email : </label>
        <input type="text" name="username" required value={data.username} onChange={handleinput}></input>
        <label> password : </label>
        <input type="password" name="password" required value={data.password} onChange={handleinput}></input>
        <input type="submit" className="submit" onClick={PostData}></input>
        </div>
        </>
    )
};


export default SignUp;
