import React, { useState } from "react";
import {Figure, Table} from 'react-bootstrap';
import { Content } from "./NavBar";

const Anime = () => {
  const [item5, setitem5] = useState([])
  const [Name, setName] = useState([])
    return(
        <>
         <Content.Consumer>
          {(item5) => {
               setitem5(item5) 
          }}
           </Content.Consumer>
     

     <div className="Main2">
<Table bordered size="sm">
<thead>
<tr>
<th style={{background:'#f6f6a9'}}>
<h1> Popular on Anime World </h1>
</th>
</tr>
</thead>
<tbody>
<tr>
<td>
<div className="PopularAnime"> 
<div className="Main3">
{item5.map((item5, index) => {
                return <Figure key={index} className="p-2">
                <Figure.Image width={250} height={300} alt="171x180" src={`/Images/AnimeImages/${item5}.jpg`}/>
                <Figure.Caption style={{textAlign: 'center'}}>
                  {item5}
                </Figure.Caption>
              </Figure>
              })}
              </div>  
              </div>  
          </td>
          </tr>
          </tbody>
          </Table>
</div>

    </>)
} 

export default Anime;
