import React, { useState } from "react";
import {Figure, Table} from 'react-bootstrap';
import { Content, ContentName } from "./NavBar";

const Anime = () => {

  const [item5, setitem5] = useState([])
  const [Name, setName] = useState([])  
    return(
        <>
         <Content.Consumer>
          {(item5) => {
              return <ContentName.Consumer>
             {(Name) => {
               setitem5(item5)
               setName(Name) 
             }}
           </ContentName.Consumer> 
          }}
           </Content.Consumer>
           
    <div className="Main2">
<Table bordered size="sm">
<thead>
<tr>
<th style={{background:'#f6f6a9'}}>
<h1> Popular {Name} on Anime World </h1>
</th>
</tr>
</thead>
<tbody>
<tr>
<td>
<div className="PopularAnime"> {item5.map((item5, index) => {
                return <Figure key={index} className="p-2">
                <Figure.Image width={250} height={300} alt="171x180" src={`/Images/AnimeImages/${item5}.jpg`}/>
                <Figure.Caption style={{textAlign: 'center'}}>
                  {item5}
                </Figure.Caption>
              </Figure>
              })}
              </div>    
          <div className="hidden">
          {item5.slice(2, 6).map((item5, index) => {
            return <Figure key={index} className="p-2">
            <Figure.Image width={280} height={300} alt="171x180" src={`/Images/AnimeImages/${item5}.jpg`}/>
            <Figure.Caption style={{textAlign: 'center'}}>
              {item5}
            </Figure.Caption>
          </Figure>
          })}
          </div>
          </td>
          </tr>
          </tbody>
          </Table>
</div>
    </>)
} 

export default Anime;
