import React,  { useState } from 'react';

function SignUp () {
    const [user, setUser] = useState({ name:"", username:"", password:""});
    let name , value;
    const handleinput = (e) => {
        console.log(e)
        name = e.target.name;
        value = e.target.value;
        setUser({...user, value})
    };



    return (
        <>
        <div className="Form"> 
        <h1>Form</h1> 
        <label> Name : </label>
        <input type="text" name="sname" required value={user.sname} onChange={handleinput} ></input>
        <label> Username or Email : </label>
        <input type="text" name="susername" required value={user.susername} onChange={handleinput}></input>
        <label> password : </label>
        <input type="password" name="spassword" required value={user.spassword} onChange={handleinput}></input>
        <input type="submit" className="submit"></input>
        </div>
        </>
    )
};


export default SignUp;
