import React,  { useState } from 'react';
const axios = require('axios').default;

function SignUp () {
    const [data, setUser] = useState({ name:"", username:"", password:""});
    let name , value;
    const handleinput = (e) => {
        console.log(e)
        name = e.target.name;
        value = e.target.value;
        setUser({...data, [name]:value})
    };

    const PostData = async (e) => {
        e.PreventDefault();
        const {name, email, password} = data;
        axios.post('/Server', data)
          .then(function (response) {
            console.log(response);
          })
          .catch(function (error) {
            console.log(error);
          });
    }

    return (
        <>
        <div className="Form" method="POST" onSubmit={PostData}> 
        <h1>Form</h1> 
        <label> Name : </label>
        <input type="text" name="name" required value={data.name} onChange={handleinput} ></input>
        <label> Username or Email : </label>
        <input type="text" name="username" required value={data.username} onChange={handleinput}></input>
        <label> password : </label>
        <input type="password" name="password" required value={data.password} onChange={handleinput}></input>
        <input type="submit" className="submit"></input>
        </div>
        </>
    )
};


export default SignUp;
